import React from 'react';

function Personalaccount() {

    function Personal() {

        const login = document.getElementById('login').value
        const password = document.getElementById('password').value
        const email = document.getElementById('email').value
        const data = {
            login: login,
            password: password,
            email: email,
                       
        }    
        console.log(data)

        const api = 'http://localhost:9001/personalaccount'

        fetch(api, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(data)
        })
        .then(result => result.json())
        .then((result) => {
          console.log(result)
        })
    }

  return (
    <> 
       <h1>Личный кабинет</h1>
       <input id='login' type='text' placeholder='Логин'/> 
       <input id='password' type='password' placeholder='Пароль'/> 
       <input id='email' type='email' placeholder='Почта'/>
       <button onClick={Personal}>Войти</button>
       <h4>Вы успешно вошли в личный кабинет!</h4>
       <h4>Хотите изменить пароль?</h4>
       <input id='login' type='text' placeholder='Введите старый пароль'/> 
       <input id='login' type='text' placeholder='Введите новый пароль'/> 
       <input id='login' type='text' placeholder='Повторите новый пароль'/> 
       <button onClick={Personalaccount}>Сохранить</button>
       
    </>    
  );
  
} 
  
export default Personalaccount;
